  state = {
    posts: []
  }

  componentDidMount() {
    axios.get('https://jsonplaceholder.typicode.com/posts?_limit=10')
      .then(res => this.setState({
        posts: res.data
      }))
  }

  delPost = (id) => {
    axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`)
      .then(res => {
        let newState = {
          posts: this.state.posts.filter((post) => {
            return post.id !== id
          })
        }

        this.setState(newState)
      })
  }

 addPost = (title) => {
    axios.post('https://jsonplaceholder.typicode.com/posts', {
      title,
      completed: false
    }).then(res => {
      console.log(res)
      this.setState({ posts: [...this.state.posts, res.data] }); //前後同步更新資料
    })
  }

  render(){
  return(



    <Router>
      <div>
       <Navbar />
       <Route exact path="/" render={props => (
          <React.Fragment>
            <Header title={this.state1.header} subtitle={this.state1.subtitle} />
            <Post todos={this.state.todos}  delTodo={this.delTodo} />
          </React.Fragment>
       )}/>


        <Route path="/new" component={New} addPost={this.addPost}/>
        <Route path="/about" component={About} />
      </div>
    </Router>



  )
  }
}

export default App;
