            <div className="post">
                <div className="container">

                    <pre>
                        {this.props.post.body}
                    </pre>
                    <button className="btn" onClick={this.props.delPost.bind(this, id)}>Delete</button>
                </div>
            </div>
        </div>
        )
    }
}

export default OpenPost
