import React, {Component} from 'react';
import './App.css';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import axios from 'axios';
import Todos from './components/Todos'
import AddTodo from './components/AddTodo';
import Header from './components/layout/Header'
import About from './components/pages/About'
class App extends Component{
  state = {
    todos: []
  }
  componentDidMount() {
    axios.get('https://jsonplaceholder.typicode.com/todos?_limit=10')
      .then(res => this.setState({ todos: res.data }))
  }

  //function for deleting the list items
  delTodo = (id) => {
    axios.delete(`https://jsonplaceholder.typicode.com/todos/${id}`)
      .then(res => {
        let newState = {
          todos: this.state.todos.filter((todo) => {
            return todo.id != id
          })
        }

        this.setState(newState)
      })
  }

  //function for marking the list items
  markcompleted=(id)=>{
    let newState={
      todos:this.state.todos.map((todo)=>{
        if(todo.id === id){
          todo.completed = !todo.completed
        }
        return todo
      })
    }
    this.setState(newState)
  }

  addTodo = (title) => {
    axios.post('https://jsonplaceholder.typicode.com/todos', {
      title,
      completed: false
    }).then(res => {
      console.log(res)
      this.setState({ todos: [...this.state.todos, res.data] }); //前後同步更新資料
    })
  }

  render(){
  return(
    <Router>
      <div className="App">
        <div className="container">
          <Header />
          <Route exact path="/" render={props => (
            <React.Fragment>
              {/* 傳給AddTodo */}
              <AddTodo addTodo={this.addTodo} />
              <Todos todos={this.state.todos} markcompleted={this.markcompleted} delTodo={this.delTodo} />
            </React.Fragment>
          )} />
          <Route path="/about" component={About} />

        </div>
      </div>
    </Router>
  )
  }
}

export default App;
