import React, {
    Component
} from 'react'
import {
    Link
} from 'react-router-dom';
export class PostItem extends Component {
    render() {
        const {
            id,
            title,
            body
        } = this.props.post;
        return ( <
            div className = "post" >
            <
            div className = "container" >
            <
            Link className = "link"
            to = "/openpost" > < h2 > {
                this.props.post.title
            } < /h2></Link >
            <
            pre > {
                this.props.post.body
            } <
            /pre> <
            button className = "btn"
            onClick = {
                this.props.delPost.bind(this, id)
            } > Delete < /button> <
            /div> <
            /div>
        )
    }
}

export default PostItem;
