import React, { Component } from 'react'
import Header from './layout/Header'
import axios from 'axios';
export class OpenPost extends Component {
    componentDidMount() {
        axios.get(`https://jsonplaceholder.typicode.com/posts/${post.id}?sort=id&_order=desc&_limit=5`)
            .then(res => this.setState({
                posts: res.data
            }))
    }
    render() {

        return (
            <div className="post">
                <p>112</p>
                <Header title={this.props.post.title} subtitle={this.props.post.body} />
                <div className="container">
                    <pre>
                        {this.props.post.body}
                    </pre>
                </div>
            </div>
        )
    }
}

export default OpenPost;
