      title:"t1",
      complated:true
    },
    {
      id: 2,
      title: "t2",
      complated: false
    },
    {
      id: 3,
      title: "t3",
      complated: false
    },
  ]
  }
  markcompleted=(id)=>{
    let newState={
      todos:this.state.todos.map((todo)=>{
        if(todo.id==id){
          todo.completed = ! todo.completed
        }
        return todo
      })
    }
  }
  render(){
  return(
    <div className="App">
      <h1>MY TODO</h1>
      <Todos todos={this.state.todos} markcomplete={this.markcomplete}/>
    </div>
  )
  }
}

export default App;
